import React, {Component} from "react";
import { Button, Form, FormGroup, Label, Input } from "reactstrap";
import Sneaker from "./Sneaker.js";
import {
  Row, Col, Container
} from 'react-bootstrap';

class Sneakers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: [
        {
          userProfile:{
            email: ''
          },
          sneakers: [],
        }
      ],
      seeSneakers: false,
      users: localStorage.getItem("user")
    }
  }
  componentDidMount = () => {
    this.getSneakers();

  }

  getSneakers = () => {
    console.log(this.state.user)
    fetch("http://localhost:8080/user/list",{
      method: "Get",
      headers: new Headers({
        'Authorization': "Bearer " + this.state.users,
        "Content-Type": "application/json"
      })
    })

    .then(res => {
      // console.log(res.json())
      return res.json()
      // set the state to be an array of sneakers
    })
    .then(res => {
      this.setState({
        user: [
          {
            userProfile:{
              email: res
            },
            sneakers: res,
          }
        ]})
      return res;
    })
    .catch(err => {
      console.log(err);
    });
  }

  render() {
    return (
      <div>
      <Container className="allSneaker">
      <Row>
        <Col>Brand</Col>
        <Col>Name</Col>
        <Col>Gender</Col>
        <Col>Size</Col>
        <Col>Condition</Col>
        <Col>Release Date</Col>
        <Col>Retail Price</Col>
        <Col>Resale price</Col>
      </Row>
      </Container>
         {this.state.user.sneakers.length > 0 && this.state.sneakers.map(sneaker => {
          return (

            <Sneaker
             brand = {sneaker.brand}
             name = {sneaker.name}
             gender = {sneaker.gender}
             size = {sneaker.size}
             condition = {sneaker.condition}
             releaseDate = {sneaker.releaseDate}
             retailPrice = {sneaker.retailPrice}
             resalePrice = {sneaker.resalePrice}
             />
          )

        })}
      </div>
    )
  }
}

export default Sneakers;
