import React, {Component} from "react";
import { Button, Form, FormGroup, Label, Input } from "reactstrap";
import Clothing from "./Clothing.js";
import {
  Row, Col, Container
} from 'react-bootstrap';

class Clothes extends Component {
  constructor(props) {
    this.state = {
    user: [],
    seeSneakers: false,
    users: localStorage.getItem("user")
  }
}
usersArr = [];
  componentDidMount = () => {
    this.getClothes();

  }

  getClothes = () => {
    fetch("http://localhost:8080/clothes/list/all",{
      method: "Get",
      headers: new Headers({
        'Authorization': "Bearer " + this.state.users,
        "Content-Type": "application/json"
      })
    })

    .then(res => {
      // console.log(res.json())
      return res.json()
      // set the state to be an array of sneakers
    })
    .then(res => {
      this.setState({user: res});
      console.log("first user id "+this.state.user[0].sneakers[0].brand)
      return res;
    })
    .catch(err => {
      console.log(err);
    });

  }

  render() {
    return (
      <div>
        <Container className="allSneaker">
        <Row>
          <Col>Brand</Col>
          <Col>Name</Col>
          <Col>Gender</Col>
          <Col>Size</Col>
          <Col>Condition</Col>
          <Col>Retail Price</Col>
          <Col>Rental price</Col>
          <Col>E-mail</Col>
        </Row>
        </Container>

          { this.state.user.length > 0 && this.state.user.map(currUser => {
              return currUser.clothes.length>0 && currUser.clothes.map(clothing=>{
                return (
                  <div>
                  <Clothing
                   brand = {clothing.brand}
                   name = {clothing.name}
                   color = {clothing.gender}
                   gender = {clothing.size}
                   size = {clothing.condition}
                   retailPrice = {clothing.retailPrice}
                   rentalPrice = {clothing.rentalPrice}
                   email = {currUser.userProfile.email}
                   />
                   </div>
                )
              })
          })}

      </div>
    )
  }
}

export default Clothes;
